import './Modal.css'

const Modal = ({ content, cb }) => {
  return (
    <div className='modal-container'>
        <div className='modal-header'>
            <h3 className='modal-title'>{content.title}</h3>
            <button onClick={cb}>X</button>
        </div>

        <div className='modal-content-section'>
            {content.text}
        </div>
    </div>
  )
}

export default Modal
