import './App.css';
import { useState } from 'react'
import google from './google-play-badge.png'
import apple from './app-store.svg'
import character from './character.png'
import Modal from './Components/Modal';

function App() {

  const [showPolicyModal, setShowPolicyModal] = useState(false);
  const [showSupportModal, setShowSupportModal] = useState(false);

  function handleShowPolicyModal() {
    setShowPolicyModal(true)
  }

  function handleClosePolicyModal() {
    setShowPolicyModal(false)
  }
  function handleShowSupportModal() {
    setShowSupportModal(true)
  }

  function handleCloseSupportModal() {
    setShowSupportModal(false)
  }

  return (
    <div className='container'>
      <div className='hero'>
        <h1>StorySprout</h1>
        <p>Never-ending stories for kids and their big people.</p>
      </div>

      <div className='button-container'>
        <img src={google} alt='play store link' className='play-store-link'/>
        <img src={apple} alt='app store link' className='app-store-link'/>
      </div>

      <div className="character">
        <img src={character} alt='character' className="character-img"/>
      </div>

      <div className='footer'>
        <a onClick={handleShowPolicyModal}>Privacy Policy</a>
        <a onClick={handleShowSupportModal}>Support</a>
      </div>

      {showPolicyModal && <Modal modalTitle='Privacy Policy' handleClosePolicyModal={handleClosePolicyModal} />}


    </div>
  );
}

export default App;
